import React from 'react'
import HeaderBar from '../components/HeaderBar'
import FootBar from '../components/FootBar'
import '../styles/Slido.css'
import a from '../data/shrek/1.png'
import b from '../data/shrek/2.png'
import c from '../data/shrek/3.png'
import d from '../data/shrek/4.png'
import e from '../data/shrek/5.png'
import f from '../data/shrek/6.png'
import g from '../data/shrek/7.png'
import h from '../data/shrek/8.png'

export default function Slido () {
    const [results, setResults] = React.useState([
        <img className="slido-blocks" src={a} alt="slido-img0" name="0" onClick={handleClick} />,
        <img className="slido-blocks" src={b} alt="slido-img1" name="1" onClick={handleClick} />,
        <img className="slido-blocks" src={c} alt="slido-img2" name="2" onClick={handleClick} />,
        <img className="slido-blocks" src={d} alt="slido-img3" name="3" onClick={handleClick} />,
        <img className="slido-blocks" src={e} alt="slido-img4" name="4" onClick={handleClick} />,
        <img className="slido-blocks" src={f} alt="slido-img5" name="5" onClick={handleClick} />,
        <img className="slido-blocks" src={g} alt="slido-img6" name="6" onClick={handleClick} />,
        <img className="slido-blocks" src={h} alt="slido-img7" name="7" onClick={handleClick} />,
        <div className="slido-blocks" name="8" ></div>
    ])
    const [game, setGame] = React.useState([])
    const connected = {
        0: [1, 3],
        1: [0, 2, 4],
        2: [1, 5],
        3: [0, 4, 6],
        4: [1, 3, 5, 7],
        5: [2, 4, 8],
        6: [3, 7],
        7: [4, 6, 8],
        8: [7, 5]
    }

    function shuffleArray(array) {
        const newArr = array.slice()
        for (let i = newArr.length - 1; i > 0; i--) {
            const rand = Math.floor(Math.random() * (i + 1));
            [newArr[i], newArr[rand]] = [newArr[rand], newArr[i]];
        }
        return newArr
    }

    React.useEffect(() => {
        setGame(shuffleArray(results))
    }, [])

    const blocks = game.map((ele, idx) => {
        if (ele === '') {
            return <div className="slido-blocks" name={idx} ></div>
        }
        return <img className="slido-blocks" src={ele} name={idx} onClick={handleClick} />
    })

    function handleClick (event) {
        const { name } = event.target
        console.log('click', name)
    }

    return (
        <div className="page-container">
            <HeaderBar />
            <div className="container">
                <div id="slido-container">
                    <div className="slido-block-rows">
                        {blocks[0]}
                        {blocks[1]}
                        {blocks[2]}
                    </div>
                    <div className="slido-block-rows">
                        {blocks[3]}
                        {blocks[4]}
                        {blocks[5]}
                    </div>
                    <div className="slido-block-rows">
                        {blocks[6]}
                        {blocks[7]}
                        {blocks[8]}
                    </div>
                </div>
                <div id="slido-btn-container">
                    <button id="slido-solve-btn" onClick={() => setBlocks(results)}>Solve</button>
                    <button id="slido-reset-btn" onClick={() => setBlocks(shuffleArray(results))}>Reset</button>
                </div>
            </div>
            <FootBar />
        </div>
    )
}
