import React from 'react'
import HeaderBar from './HeaderBar'
import FootBar from './FootBar'

export default function Dashboard () {
    const [nGames, setNGames] = React.useState(0)
    const [initNGames, seInitNGames] = React.useState(0)

    async function getScore () {
        return fetch('http://cgi.cse.unsw.edu.au/~cs6080/data/score.json', {
            method: 'GET'
        })
        .then(data => data.json())
    }

    React.useEffect(() => {
        const score = JSON.parse(sessionStorage.getItem('score'))
        async function setScore () {
            const iniScore = await getScore()
            console.log('ini', iniScore, iniScore.score)
            setNGames(iniScore.score)
            seInitNGames(iniScore.score)
            localStorage.setItem('score', JSON.stringify(iniScore.score))
        }
        if (score === undefined) {
            console.log('undefined')
            setScore()
        } else {
            setNGames(score)
        }
    }, [])

    function resetValue () {
        setNGames(initNGames)
    }

    return (
        <div className="page-container">
            <HeaderBar />
            <div className="container">
                <div id="infos">
                    <div id="main-title">
                        Please choose an option from the navbar.
                    </div>
                    <div id="sub-title">
                        Games won: {nGames} <button onClick={resetValue}>(reset)</button>
                    </div>
                </div>
            </div>
            <FootBar />
        </div>
    )
}
