import React from 'react'
import HeaderBar from '../components/HeaderBar'
import FootBar from '../components/FootBar'
import { strs } from '../data/blanko.js'

export default function Blanko () {
    const [currStr, setCurrStr] = React.useState('')
    const [emptyThree, setEmptyThree] = React.useState([0, 1, 2])

    function getRandomFromArray (array) {
        const randIdx = Math.floor(Math.random() * array.length)
        return array[randIdx]
    }

    function getEmptyIndex () {
        let possibleIdx = []
        for (let i = 0; i < currStr.length; i++) {
            if (currStr[i] !== ' ') {
                possibleIdx.push(i)
            }
        }
        console.log('possibleIdx', possibleIdx)
        const emptyIdx = []
        for (let i = 0; i < 3; i++) {
            const choosed = getRandomFromArray(possibleIdx)
            possibleIdx = possibleIdx.filter(ele => ele !== choosed)
            emptyIdx.push(choosed)
        }
        console.log('emptyIdx', emptyIdx)
    }

    function initGame () {
        const randomStr = getRandomFromArray(strs)
        setCurrStr(randomStr)

        const emptyIdx = getEmptyIndex()
        setEmptyThree(emptyIdx)
    }

    React.useEffect(() => {   
        initGame()
    })

    const getBlocks = currStr.map((ele, idx)=> {
        if (emptyThree.includes(idx)) {
            return (
                <div className="blanko-blocks">
                    <input type="text" className="blanko-input-box"></input>
                </div>
            )
        }
        return (
            <div className="blanko-blocks">
                {ele}
            </div>
        )
    })

    function handleReset () {
        console.log('handle Reset')
    }

    return (
        <div className="page-container">
            <HeaderBar />
            <div className="container">
                {getBlocks()}
                <div id="block-container">
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                    <div className="blanko-blocks"></div>
                </div>
                <button id="blanko-reset-btn" onClick={handleReset}>RESET</button>
            </div>
            <FootBar />
        </div>
    )
}
