import React from 'react'
import HeaderBar from '../components/HeaderBar'
import FootBar from '../components/FootBar'
import '../styles/Tetro.css'

export default function Tetro () {
    const [ board, setBoard ] = React.useState([])
    const [ gameStart, setGameStart ] = React.useState(false)
    let currBlock = []
    const types = [
        [[0, 0], [0, 1], [1, 0], [1, 1]],   // 2 x 2
        [[0, 0]],   // 1 x 1
        [[0, 0], [0, 1]]    // 2 x 1 
    ]

    function getRandomFromArray (array) {
        const randIdx = Math.floor(Math.random() * array.length)
        return array[randIdx]
    }

    function setInitBoard () {
        const initBoard = []
        for (let i = 0; i < 12; i++) {
            const newRow = []
            for (let j = 0; j < 10; j++) {
                newRow.push(0)
            }
            initBoard.push(newRow)
        }
        setBoard(initBoard)
    } 

    React.useEffect(() => {
        setInitBoard()
        // eslint-disable-next-line
        document.addEventListener('keydown', handleKeyDown)
        // eslint-disable-next-line
    }, [])

    const blocks = board.map((row, rowIdx) => {
        console.log('enter blocks', currBlock, board)
        const blockRows = row.map((cell, colIdx) => {
            if (cell === 0) {
                return <div className="board-cells" name={rowIdx} value={colIdx}></div>
            } else if (cell === 1) {
                return <div className="occupied-board-cells" name={rowIdx} value={colIdx}></div>
            } else {
                return <div className="finished-board-cells" name={rowIdx} value={colIdx}></div>
            }
        })
        return (
            <div className="board-rows" key={rowIdx}>
                {blockRows}
            </div>
        )
    })

    function handleReset () {
        console.log('handleReset')
        setGameStart(false)
        setInitBoard()
    }

    // show curr block to board
    function blockProjection (rawblocks) {
        console.log('enter blockProjection', currBlock)
        const newBoard = board.slice()
        newBoard.map(row => {
            row.map(cell => {
                if (cell === 1) {
                    return 0
                }
                return cell
            })
            return row
        })

        for (let idx = 0; idx < rawblocks.length; idx++) {
            newBoard[rawblocks[idx][0]][rawblocks[idx][1]] = 1
        }
        console.log(newBoard)
        setBoard(newBoard)    
    }

    function handleKeyDown (event) {
        console.log('enteer handleKeyDown', event.keyCode, gameStart, currBlock)
        // if (!gameStart) {
        //     return
        // }

        if (event.keyCode === 37) {
            // left
            console.log('press left key', currBlock)

            // move to left
            const tmpBlock = currBlock.map(ele => {
                return [ele[0], ele[1] - 1]
            })
            currBlock = tmpBlock
            // setCurrBlock(tmpBlock)            
            blockProjection(tmpBlock)
        } else if (event.keyCode === 39) {
            // right
            console.log('press right key', currBlock)

            // move to right
            const tmpBlock = currBlock.map(ele => {
                return [ele[0], ele[1] + 1]
            })
            currBlock = tmpBlock
            // setCurrBlock(tmpBlock) 
            blockProjection(tmpBlock)
        }
    }

    function handleClick () {
        console.log('enter handleClick', gameStart)
        if (gameStart) {
            console.log('return')
            return
        }
        setGameStart(true)
        // console.log('startstart', gameStart)

        // show new block
        const randomStart = getRandomFromArray(types)
        currBlock = randomStart
        // setCurrBlock(randomStart)
        blockProjection(randomStart)
    }

    return (
        <div className="page-container">
            <HeaderBar />
            <div className="container">
                <div id="board-container" onClick={handleClick}>
                    {blocks}
                </div>
                <button id="tetro-reset-btn" onClick={handleReset}>
                    RESET
                </button>
            </div>
            <FootBar />
        </div>
    )
}
